    @Test
    public void printPatternForSentence() {

        DependencyParse dependencyParse = DependencyParse.parseJson(sentence1);

        Entity entity1 = EntityFinderUtility.locateEntityInParse(dependencyParse, "Einstein");
        Entity entity2 = EntityFinderUtility.locateEntityInParse(dependencyParse, "Berlin");

        BinaryPatternExtractor extractor = new BinaryPatternExtractor(ExtractorType.EXPLORATORY_IE);

        printPattern(extractor, sentence1, entity1.getText(), entity2.getText());

    }

